import { createServer } from "http";
import { z } from "zod";
var contactSchema = z.object({
  name: z.string().min(2, "Nome deve ter pelo menos 2 caracteres"),
  email: z.string().email("Email inv\xE1lido"),
  phone: z.string().min(10, "Telefone deve ter pelo menos 10 d\xEDgitos"),
  specialty: z.string().min(1, "Especialidade \xE9 obrigat\xF3ria")
});
async function registerRoutes(app2) {
  app2.post("/api/contact", async (req, res) => {
    try {
      const validatedData = contactSchema.parse(req.body);
      console.log("Contact form submission:", validatedData);
      res.json({
        success: true,
        message: "Formul\xE1rio enviado com sucesso! Entraremos em contato em at\xE9 24 horas."
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({
          success: false,
          message: "Dados inv\xE1lidos",
          errors: error.errors
        });
      } else {
        console.error("Contact form error:", error);
        res.status(500).json({
          success: false,
          message: "Erro interno do servidor"
        });
      }
    }
  });
  const httpServer = createServer(app2);
  return httpServer;
}

